import React, { Component } from "react";
import Icon from "@expo/vector-icons/MaterialCommunityIcons";
import { ScrollView, TouchableOpacity } from "react-native-gesture-handler";
import {
  View,
  Text,
  Image,
  ImageBackground,
  TextInput,
  StyleSheet,
  Button,
} from "react-native";
import { SafeAreaView } from "react-native-safe-area-context";

export default class Login extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    const [value, onChangeText] = React.useState("");
    return (
      <ImageBackground
        style={styles.backgroundStyle}
        blurRadius={0}
        source={require("../../assets/background_login.jpg")}
      >
        <Image
          source={require("../../assets/logo.png")}
          style={styles.logo}
        ></Image>
        <Text style={styles.header}>Welcome to Fitz</Text>
        <TextInput
          style={styles.input}
          onChangeText={(text) => onChangeText(text)}
          value={value}
        />
        <TextInput
          style={styles.input}
          onChangeText={(text) => onChangeText(text)}
          value={value}
        />
        <Button style={styles.button} title={"Login"}></Button>
        <Button style={styles.button} title={"Register"}></Button>
      </ImageBackground>
    );
  }
}

const styles = StyleSheet.create({
  input: { backgroundColor: "red" },
  button: {
    width: "100%",
    alignSelf: "stretch",
  },
  backgroundStyle: {
    flex: 1,
    justifyContent: "center",
    width: "100%",
    height: "100%",
    textAlign: "center",
  },
  header: {
    fontFamily: "Roboto",
    fontSize: 50,
    fontWeight: "bold",
    color: "white",
  },
  logo: {
    width: 300,
    alignSelf: "center",
    height: 300,
  },
});
