import React, { Component } from "react";
import Icon from "@expo/vector-icons/MaterialCommunityIcons";
import { ScrollView, TouchableOpacity } from "react-native-gesture-handler";
import {
  View,
  Text,
  Image,
  ImageBackground,
  StyleSheet,
  Button,
  TextInput,
} from "react-native";
import { SafeAreaView } from "react-native-safe-area-context";

const Login = () => {
  const [username, onChangeUsername] = React.useState("");
  const [password, onChangePassword] = React.useState("");

  return (
    <ImageBackground
      style={styles.backgroundStyle}
      //blurRadius={0}
      //source={require("../../assets/background_login.jpg")}
    >
      <Image
        source={require("../../assets/logo.png")}
        style={styles.logo}
      ></Image>
      <Text style={styles.header}>STAY FITZ</Text>
      <TextInput
        style={styles.input}
        onChangeText={(text) => onChangeUsername(text)}
        value={username}
      />
      <TextInput
        secureTextEntry={true}
        style={styles.input}
        onChangeText={(text) => onChangePassword(text)}
        value={password}
      />
      <Button style={styles.button} title={"Login"}></Button>
      <Button style={styles.button} title={"Register"}></Button>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  input: {
    backgroundColor: "blue",
    alignSelf: "center",
    flex: 0.08,
    padding: 10,
    borderRadius: 10,
    width: "80%",
  },
  button: {
    width: "100%",
    alignSelf: "stretch",
    flex: 0.5,
    padding: "10",
  },
  backgroundStyle: {
    flex: 1,
    flexDirection: "column",
    justifyContent: "space-between",
    width: "100%",
    height: "100%",
    textAlign: "center",
    backgroundColor: "#F4743B",
    // transform: [{ rotate: "20deg" }],
  },
  header: {
    fontFamily: "Roboto",
    fontSize: 50,
    fontWeight: "bold",
    color: "white",
    alignSelf: "center",
    flex: 0.2,
  },
  logo: {
    width: 300,
    alignSelf: "center",
    height: 300,
    flex: 0.5,
  },
});
export default Login;
