import React, { Component } from "react";
import Icon from "@expo/vector-icons/MaterialCommunityIcons";
import { ScrollView, TouchableOpacity } from "react-native-gesture-handler";
import {
  View,
  Text,
  Image,
  ImageBackground,
  StyleSheet,
  Button,
  TextInput,
} from "react-native";
import { SafeAreaView } from "react-native-safe-area-context";

const Login = () => {
  const [value, onChangeText] = React.useState("Useless Placeholder");
  return (
    <ImageBackground
      style={styles.backgroundStyle}
      //blurRadius={0}
      //source={require("../../assets/background_login.jpg")}
    >
      <Image
        source={require("../../assets/logo.png")}
        style={styles.logo}
      ></Image>
      <Text style={styles.header}>STAY FITZ</Text>
      <TextInput
        style={styles.input}
        onChangeText={(text) => onChangeText(text)}
        value={value}
      />
      <TextInput
        style={styles.input}
        onChangeText={(text) => onChangeText(text)}
        value={value}
      />
      <Button style={styles.button} title={"Login"}></Button>
      <Button style={styles.button} title={"Register"}></Button>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  input: { backgroundColor: "red", flex: 0.1, height: 20 },
  button: {
    width: "100%",
    alignSelf: "stretch",
    flex: 0.5,
  },
  backgroundStyle: {
    flex: 1,
    justifyContent: "center",
    width: "100%",
    height: "100%",
    textAlign: "center",
    backgroundColor: "#F4743B",
    // transform: [{ rotate: "20deg" }],
  },
  header: {
    fontFamily: "Roboto",
    fontSize: 50,
    fontWeight: "bold",
    color: "white",
    alignSelf: "center",
    flex: 0.2,
  },
  logo: {
    width: 300,
    alignSelf: "center",
    height: 300,
    flex: 0.5,
  },
});
export default Login;
